function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');

if(newValue == 'human resources')
	{
		g_form.setValue('u_what_needed','test1','test1');
		g_form.setValue('u_what_needed','test2','test2');
	}
	else if(newValue == 'facilities'){
		g_form.setValue('u_what_needed','test31','test31');
		g_form.setValue('u_what_needed','test4','test4');
	}
   else if(newValue == 'legal'){
		g_form.setValue('u_what_needed','test5','test3');
		g_form.setValue('u_what_needed','test6','test4');
	}
}
